import React from 'react';
import MovieAppHeader from './movieAppHeader';
import MovieCardList from './movieCardList';
import NoData from '../../common/nodata';

class MovieAppWrapper extends React.Component {

    constructor(props) {
        super()
        this.state = { movieList: [] ,movieDetails: '',isSearched: false}
        this.search = this.search.bind(this);

    }

    search(searchAttributes) {
        var url = 'http://www.omdbapi.com/?apikey=b678de0b&t='+searchAttributes.title+'&y='+searchAttributes.year
        fetch(url)
            .then(res => {
                return res.json();
            }).then(data => {
                this.setState({movieDetails: data, isSearched: true})
            }).then(error => {
                console.log(error);
            })
    }

    render() {
        const { movieList, movieDetails, isSearched } = this.state;
        return (
            <React.Fragment>
            <div className="clearfix"></div>
            <div className={!isSearched ? 'background-image': ''}>
                <MovieAppHeader search={this.search}/>
            </div>

                {movieDetails  && !movieDetails.Error &&  <MovieCardList movieDetails={movieDetails} />}

                {((!movieDetails || movieDetails.Error)  && isSearched) && this.showSearchErro()}

                {/* {!isSearched && <NoData message={'Enter title to Search Movie'} backgroundImageSrc={'https://static.videomaker.com/wp-content/uploads/2020/01/clipper-4223871_960_720.jpg'}/>} */}

            </React.Fragment>
        )
    }

}

export default MovieAppWrapper;
