import React from 'react';
import { Link } from 'react-router-dom';

class Navbar extends React.Component {

    constructor() {
        super()
        this.state = {activeLink: 'home'}
        this.handleMenuClick = this.handleMenuClick.bind(this);
    }

    handleMenuClick(navLink) {
        this.setState({activeLink: navLink == '/' ? 'home': 'navbar'})
        window.location.href = navLink;
    }

    render() {
        return (
            <div className='nav-bar-outer'>
                <ul className='top-nav-ul'>
                    <Link className="nav-item-left"  to="/">Home</Link>
                    <Link className="nav-item-right"  to="/navbar">Home</Link>
                    {/* <li className="nav-item-left"><a onClick={() => this.handleMenuClick('/')}>Home</a></li> */}
                    {/* <li className="nav-item-right"><a onClick={() => this.handleMenuClick('/navbar')}>Favourite</a></li> */}
                </ul>
            </div>
        )
    }
}

export default Navbar
