import React from 'react';

function MovieCard(props) {
    return (
        <div className={props.outerCardClass}>
                    <div className="card">
                        <img class="card-img" src={props.currentItem.Poster} alt="Avatar" />
                        <div className="container">
                              <h4><b>{props.currentItem.Title}</b></h4> 
                              <p>{props.currentItem.Released}</p>
                              {this.props.enableSelectFavoute && <button className="card_btn" onClick={this.toggleFavourite}>{this.movieIndexInFavList() != -1 ? 'Unfavourite' : 'Favourite'}</button>}
                        </div>
                    </div>
            </div>
    )
}

export default MovieCard;
