import React from 'react';
import ToastMessage from '../../common/toastMessage';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const searchTypeOptions = [{text:'Movie',value: 'movie'},{text: 'Series',value: 'series'},{text:'Episode',value:'episode'}]

class MovieAppHeader extends React.Component {

    constructor() {
        super()
        this.state = { title: '', year: '', titleError: false , yearFormatError: false,type: searchTypeOptions[0]};
        this.onChange = this.onChange.bind(this);
        this.search = this.search.bind(this);
        this.validate = this.validate.bind(this);
        this.keyPressValidate = this.keyPressValidate.bind(this);
        toast('dsadasdsaewewqeqwewq',{position:toast.POSITION.TOP_LEFT})
    }

    onChange(e) {
        this.setState({[e.target.name]: e.target.value})
    }

    search() {
        this.props.search(this.state);
    }

    keyPressValidate(e) {
        if(e.target.name == 'year' && isNaN(e.target.value)){
            return
        }else {
            this.onChange(e);
        }
    }

    validate() {
        toast('dsadasdsaewewqeqwewq',{position:toast.POSITION.TOP_LEFT})
        let titleError = false;
        let yearRangeError = false;
        if(!this.state.title) {
            titleError = true;
        }

         // first movie came in 1888
        if(this.state.year && ((this.state.year < 1888) || (this.state.year > new Date().getFullYear()))) {
            yearRangeError = true;
        }

        if(titleError || yearRangeError) this.setState({titleError: titleError,yearRangeError: yearRangeError})
        else {this.search();}


    }

    render() {
        return (
        <React.Fragment>
            <div className="search-div">
                <div className="search-inner">
                    <label required>Title:</label>
                    <input type="text" name="title" value={this.state.title} required="required" onChange={this.onChange} />
                    {/* {this.state.titleError && <p>Title is mandatory</p>} */}
                </div>
                <div className="search-inner">
                    <label>Year:</label>
                    <input type="text" name="year" value={this.state.year} onChange={this.keyPressValidate} />
                    {this.state.yearRangeError && <p>Enter the year between 1888(First movie release) and Current Year</p>}
                </div>
                <div className="search-inner">
                    <select value={this.state.type} onChange={this.onChange} name="type">
                        {searchTypeOptions.map((item,index) => 
                            <option value={item.value}>{item.text}</option>
                        )}
                    </select>
                </div>
                <button class="dropdown" onClick={this.validate}>Search</button>
            </div>
            </React.Fragment>
        )
    }
}

export default MovieAppHeader;
