import react from 'react';

function MovieCard() {
    return (
        <div className={this.props.outerClassName}>
                    <div className="card">
                        <img class="card-img" src={this.props.movieDetails.Poster} alt="Avatar" />
                        <div className="container">
                              <h4><b>{this.props.movieDetails.Title}</b></h4> 
                              <p>{this.props.movieDetails.Released}</p>
                              <button className="card_btn" onClick={this.toggleFavourite}>{this.movieIndexInFavList() != -1 ? 'Unfavourite' : 'Favourite'}</button>
                        </div>
                    </div>
            </div>
    )
}

export default MovieCard;
